import React, { useState, useEffect} from 'react';
import "antd/dist/antd.min.css";
import { Carousel } from "antd";
import pepper from '../components/img/pepper.jpg'
import bggif from '../components/img/1oxd.gif'
import './Main.css'
//Ïä§ÏΩîÎ°§ Î™®ÏÖò
import { Animator, ScrollContainer, ScrollPage, batch, Fade, FadeIn, FadeOut, Move, MoveIn, MoveOut, Sticky, StickyIn, StickyOut, Zoom, ZoomIn, ZoomOut } from "react-scroll-motion";

//Ïù¥Í±∞ Ïä§ÏΩîÎ°§ Î™®ÏÖò
const ZoomInScrollOut = batch(StickyIn(), FadeIn(), ZoomIn());
const FadeUp = batch(Fade(), Move(), Sticky());

const contentStyle = {
  height: '300px',
  width: '93%',
  color: 'black',
  lineHeight: '160px',
  textAlign: 'center',
  BackgroundColor: 'balck',
  // paddingLeft :'10%' ,
  // paddingRight : '10%',
  marginTop: '3%',
  marginRight: '40%',
  marginLeft: '3%',
  marginBottom: '3%'
};


  
        

export default function Main() { 

  const [ScrollY, setScrollY] = useState(0);
      const [BtnStatus, setBtnStatus] = useState(false); // Î≤ÑÌäº ÏÉÅÌÉú
      
      const handleFollow = () => {
        setScrollY(window.pageYOffset);
        if(ScrollY > 50) {
          // 100 Ïù¥ÏÉÅÏù¥Î©¥ Î≤ÑÌäºÏù¥ Î≥¥Ïù¥Í≤å
          setBtnStatus(true);
        } else {
          // 100 Ïù¥ÌïòÎ©¥ Î≤ÑÌäºÏù¥ ÏÇ¨ÎùºÏßÄÍ≤å
          setBtnStatus(false);
        }
      }
    
      const handleTop = () => {  // ÌÅ¥Î¶≠ÌïòÎ©¥ Ïä§ÌÅ¨Î°§Ïù¥ ÏúÑÎ°ú Ïò¨ÎùºÍ∞ÄÎäî Ìï®Ïàò
        window.scrollTo({
          top: 0,
          behavior: "smooth"
        });
        setScrollY(0);  // ScrollY Ïùò Í∞íÏùÑ Ï¥àÍ∏∞Ìôî
        setBtnStatus(false); // BtnStatusÏùò Í∞íÏùÑ falseÎ°ú Î∞îÍøà => Î≤ÑÌäº Ïà®ÍπÄ
      }
    
      useEffect(() => {
        const watch = () => {
          window.addEventListener('scroll', handleFollow)
        }
        watch();
        return () => {
          window.removeEventListener('scroll', handleFollow)
        }
      })

  
  return(
    <div>
      <div className='Mainhead'>
        <h1>AI Î≥ëÌï¥ ÌÉêÏßÄ!</h1>
        <h3>Ïù¥ÎØ∏ÏßÄÎ•º ÏóÖÎ°úÎìúÏãú AIÍ∞Ä ÏßÅÏ†ë ÎÜçÏûëÎ¨ºÏùò Î≥ëÌï¥Î•º Ï∞æÏïÑÎÇ∏ ÌõÑ Í∑∏Ïóê ÎßûÎäî ÏÜîÎ£®ÏÖòÏùÑ Ï†úÍ≥µÌï¥ ÎìúÎ¶ΩÎãàÎã§.</h3>
      </div>
      <div className='Body1'>
        <div>
          <h1 className='carou_title'>
            Best Solution!
          </h1>
        </div>
      <Carousel dotPosition='left' autoplay>
        <div>
          <img style={contentStyle} src={pepper}></img>
        </div>
        <div>
          <img style={contentStyle} src={bggif}></img>
        </div>
        <div>
          <img style={contentStyle} src={bggif}></img>
        </div>
        <div>
          <img style={contentStyle} src={bggif}></img>
        </div>
      </Carousel>
    </div>
    <ScrollContainer>
  <ScrollPage>
    <Animator animation={batch(Fade(), Sticky(), MoveOut(0, -100))}>
      <span style={{ fontSize: "30px" }}>Ïù¥Í≤É Î¥êÎ¥êÎûë~ „Öé„Öé„Öé üòÄ</span>
    </Animator>
  </ScrollPage>
  <ScrollPage>
    <Animator animation={ZoomInScrollOut}>
      <span style={{ fontSize: "40px" }}>Ïù¥Í±∞ ÎÇòÌÉÄÎÇúÎã§~ ‚ú®</span>
    </Animator>
  </ScrollPage>
  <ScrollPage>
    <Animator animation={FadeUp}>
      <span style={{ fontSize: "40px" }}>Ïù¥Ïñç!!!! ‚õÖÔ∏è</span>
    </Animator>
  </ScrollPage>
  <ScrollPage>
    <div style={{ display: "flex", justifyContent: "center", alignItems: "center", height: "100%" }} >
      <span style={{ fontSize: "40px" }}>
        <Animator animation={MoveIn(-500, 0)}>ÏïàÎÜç~ üëãüèª</Animator>
        <Animator animation={MoveIn(500, 0)}>ÎßåÎÇòÏÑú Î∞òÍ∞ÄÏõù~! üôãüèª‚Äç‚ôÄÔ∏è</Animator>
        - ÎÇòÎäî Ï°∞ÏòÅÏ∞¨ -
        <Animator animation={MoveOut(500, 0)}>ÏûòÍ∞ÄÎ£Ω~ ‚úãüèª</Animator>
        <Animator animation={MoveOut(-500, 0)}>Îã¥Ïóê Î¥êÏö•~!~! üíõ</Animator>
      </span>
    </div>
  </ScrollPage>
  <ScrollPage>
    <Animator animation={batch(Fade(), Sticky())}>
      <span style={{ fontSize: "40px" }}>ÎÅù!</span>
      <br/>
    </Animator>
  </ScrollPage>
</ScrollContainer>
      <h1>1</h1>
      <h1>1</h1>
      <h1>1</h1>
      <h1>1</h1>
      <h1>1</h1>
      <h1>1</h1>
      <h1>1</h1>
      <h1>1</h1>
      <h1>1</h1>
      <h1>1</h1>
          <div >
          <button 
              className={BtnStatus ? "topBtn active" : "topBtn"} // Î≤ÑÌäº ÎÖ∏Ï∂ú Ïó¨Î∂Ä
              onClick={handleTop}  // Î≤ÑÌäº ÌÅ¥Î¶≠Ïãú Ìï®Ïàò Ìò∏Ï∂ú
              >TOP</button>
          </div>
</div>
    
  );
}
